phi = 0;Vmax = 18;f = 60;Vd = 1.0;Rd = 0.02;C = 100 * 10^-6;Imax = 100 * 10^-3;vFonte = @(t) abs(Vmax*cos(2*f*pi*t));vc_rhs = @(t,v) ifelse(v <= vFonte(t)-2,...         (25*vFonte(t)-50-25*v)/100e-6,...    %Valores divididos por 2*Rd = 0.04         -Imax/(100e-6));Vc = @(t,v) vc_rhs(t,v);[X_euler,Y_euler] = Euler(Vc,0,16,1/200000,10000);[X_eulermelhorado,Y_eulermelhorado] = EulerMelhorado(Vc,0,16,1/200000,10000);[X_eulermodificado,Y_eulermodificado] = EulerModificado(Vc,0,16,1/200000,10000);[X_FRK12,Y_FRK12] = FehlbergRK12(Vc,0,16,1/200000,10000);[X_FRK45,Y_FRK45] = FehlbergRK45(Vc,0,16,1/200000,10000);[X_Dormand_Prince_passoFixo, Y_Dormand_Prince_passoFixo] = RungeKutta_Dormand_Prince_ode45(Vc,0,16,1/200000,10000,1);[X_Dormand_Prince_passoAdaptativo, Y_Dormand_Prince_passoAdaptativo] = RungeKutta_Dormand_Prince_ode45(Vc,0,16,1/200000,10000,0);x = 0:0.01:0.05;y = ones(1,length(x)).*7.5;%------------------------------------FONTE----------------------------------%figure(7);xf = 0:0.0001:0.05;legenda1 = {};Vac = Vmax*cos(2*f*pi*xf+phi);Vdc = abs(Vac);hold ongrid onplot(xf,Vac);legenda1{end+1} = 'AC';plot(xf,Vdc,'--r');legenda1{end+1} = '|AC|';xlabel('t[sec]');ylabel('Vc[Volts]');title('AC');legend(legenda1,'location','northeast');hold off%---------------------------------------Vc------------------------%figure(8);hold ongrid onlegenda = {};plot(x,y,'-k');legenda{end+1} = 'Vc';plot(X_euler,Y_euler,'-r');legenda{end+1} = 'Euler'; plot(X_eulermelhorado,Y_eulermelhorado,'-g');legenda{end+1} = 'Euler melhorado';plot(X_eulermodificado,Y_eulermodificado,'-y');legenda{end+1} = 'Euler modificado';plot(X_FRK12,Y_FRK12,'-b');legenda{end+1} = 'FRK12';plot(X_FRK45,Y_FRK45,'-m');legenda{end+1} = 'FRK45';plot(X_Dormand_Prince_passoFixo,Y_Dormand_Prince_passoFixo,'-k'); legenda{end+1} = 'DPfixo';plot(X_Dormand_Prince_passoAdaptativo,Y_Dormand_Prince_passoAdaptativo,'-c');legenda{end+1} = 'DPadapt';xlabel('t[sec]');ylabel('Vc[Volts]');title('DC');legend(legenda,'location','northeast');hold off